// https://www.w3.org/2010/05/video/mediaevents.html
// https://github.com/alex-barstow/video.js/blob/9f0167b2f1dc36db318ca9ed329f8b460d8dd028/src/js/player.js#L58
type VideoJsProxiedEvent =
	| 'progress'
	| 'abort'
	| 'suspend'
	| 'emptied'
	| 'stalled'
	| 'loadedmetadata'
	| 'loadeddata'
	| 'timeupdate'
	| 'resize'
	| 'volumechange'
	| 'texttrackchange';
// https://github.com/alex-barstow/video.js/blob/9f0167b2f1dc36db318ca9ed329f8b460d8dd028/src/js/player.js
type VideoJsManagedEvent =
	| 'posterchange'
	| 'loadstart'
	| 'firstplay'
	| 'autoplay-success'
	| 'autoplay-failure'
	| 'sourceset'
	| 'ratechange'
	| 'waiting'
	| 'canplay'
	| 'canplaythrough'
	| 'playing'
	| 'seeking'
	| 'seeked'
	| 'play'
	| 'pause'
	| 'ended'
	| 'fullscreenerror'
	| 'textdata'
	| 'durationchange'
	| 'fullscreenchange'
	| 'enterFullWindow'
	| 'exitFullWindow'
	| 'disablepictureinpicturechanged'
	| 'playerreset'
	| 'volumechange'
	| 'controlsenabled'
	| 'controlsdisabled'
	| 'usingnativecontrols'
	| 'usingcustomcontrols'
	| 'error'
	| 'useractive'
	| 'userinactive'
	| 'dispose';
type VideoJsEvent = VideoJsProxiedEvent | VideoJsManagedEvent;
